INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (128, 'en', NULL, NULL, 'TRUNG THỰC', 'Tại EASY CREDIT, chúng tôi xây dựng văn hóa trung thực tại doanh nghiệp. Nhân viên được khuyến khích thẳng thắn trao đổi một cách cởi mở với đồng nghiệp, quản lý trực tiếp và các cấp lãnh đạo. Bên cạnh đó, chúng tôi cũng công khai rõ ràng kết quả công việc với những cơ hội và thách thức cụ thể để nhân viên cùng chia sẻ, đóng góp sáng kiến phát triển tổ chức.', NULL);
INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (128, 'vi', NULL, NULL, 'TRUNG THỰC', 'Tại EASY CREDIT, chúng tôi xây dựng văn hóa trung thực tại doanh nghiệp. Nhân viên được khuyến khích thẳng thắn trao đổi một cách cởi mở với đồng nghiệp, quản lý trực tiếp và các cấp lãnh đạo. Bên cạnh đó, chúng tôi cũng công khai rõ ràng kết quả công việc với những cơ hội và thách thức cụ thể để nhân viên cùng chia sẻ, đóng góp sáng kiến phát triển tổ chức.', NULL);
INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (129, 'en', NULL, NULL, 'Tôn trọng', 'Nhân viên EASY CREDIT đề cao sự tôn trọng trong văn hóa ứng xử khi làm việc. Mỗi cá nhân cần tôn trọng đồng nghiệp, tôn trọng khách hàng và đối tác để xứng đáng nhận lại sự tôn trọng từ người khác.', NULL);
INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (129, 'vi', NULL, NULL, 'Tôn trọng', 'Nhân viên EASY CREDIT đề cao sự tôn trọng trong văn hóa ứng xử khi làm việc. Mỗi cá nhân cần tôn trọng đồng nghiệp, tôn trọng khách hàng và đối tác để xứng đáng nhận lại sự tôn trọng từ người khác.', NULL);
INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (130, 'en', NULL, NULL, 'Chuyên nghiệp', 'EASY CREDIT cam kết môi trường làm việc chuyên nghiệp, tạo điều kiện để nhân viên phát huy tối đa năng lực và được công nhận xứng đáng. Chúng tôi tổ chức quy trình, phân công công việc rõ ràng và theo đúng quy tắc chuẩn mực giao tiếp của một công ty tài chính hàng đầu. Đồng thời, mỗi nhân viên tại EASY CREDIT phải là một hình mẫu chuyên nghiệp cả trong nghiệp vụ lẫn bởi tinh thần trách nhiệm, tư duy tích cực, giao tiếp và hành động nhất quán góp phần làm nên sự chuyên nghiệp của cả tổ chức.', NULL);
INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (130, 'vi', NULL, NULL, 'Chuyên nghiệp', 'EASY CREDIT cam kết môi trường làm việc chuyên nghiệp, tạo điều kiện để nhân viên phát huy tối đa năng lực và được công nhận xứng đáng. Chúng tôi tổ chức quy trình, phân công công việc rõ ràng và theo đúng quy tắc chuẩn mực giao tiếp của một công ty tài chính hàng đầu. Đồng thời, mỗi nhân viên tại EASY CREDIT phải là một hình mẫu chuyên nghiệp cả trong nghiệp vụ lẫn bởi tinh thần trách nhiệm, tư duy tích cực, giao tiếp và hành động nhất quán góp phần làm nên sự chuyên nghiệp của cả tổ chức.', NULL);
INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (131, 'en', NULL, NULL, 'Luôn hướng đến giải pháp', 'Mỗi nhân viên tại EASY CREDIT luôn được khuyến khích không ngừng học hỏi, cải tiến kể cả khi gặp phải vấn đề trong công việc. Chúng tôi luôn hướng đến giải pháp bởi vì chúng tôi biết rằng, thách thức thường đi kèm với cơ hội. Khi gặp khó khăn, nhân viên không trốn tránh trách nhiệm mà luôn tập trung tìm ra sáng kiến và hướng giải quyết phù hợp nhất cho vấnn đề để mang lại hiệu quả tốt nhất cho công việc.', NULL);
