INSERT INTO `page_block_translation` (`page_block_id`, `locale`, `photo_translation`, `url`, `name`, `description`, `content`) VALUES (149, 'en', NULL, NULL, 'GẶP GỠ NHÂN VIÊN<strong>EASY CREDIT</strong>', NULL, '<div>\r\n<p>Tại EASY CREDIT, nhân viên được xem là nhân tố quan trọng nhất không những đối với sự phát triển của doanh nghiệp mà còn để xây dựng văn hóa khác biệt trên thị trường. Vì vậy, chúng tôi luôn khuyến khích nhân viên tại EASY CREDIT xem sự nghiệp của mình như một cuộc hành trình và tận dụng tối đa những trải nghiệm từ các dự án, công việc thực tế bên cạnh những cơ hội đào tạo và phát triển của doanh nghiệp nhằm trao dồi kinh nghiệm, rèn giũa bản thân để sẵn sàng đón nhận những hội thăng tiến sự nghiệp thuận lợi nhất.</p>\r\n\r\n<p>Hãy tìm hiểu những nhân viên đang tạo dựng sự nghiệp tại Easy Credit và tham khảo những câu chuyện trải nghiệm đầy giá trị của họ.</p>\r\n</div>');
